import{_ as p,o as e,c as t,e as o}from"./app.6da7124e.js";const c={},_=o("<p>\u4EB2\u7231\u7684\u5A1C\u5A1C\uFF0C\u4F60\u597D\uFF1A</p><p>1.24 \u7B2C\u4E00\u6B21\u804A\u5929 1.28 \u52A0\u5FAE\u4FE1 1.29 \u7B2C\u4E00\u6B21\u542C\u5230\u4F60\u7684\u58F0\u97F3(\u597D\u597D\u542C\u554A) 1.30 \u7B2C\u4E00\u6B21\u8BED\u97F3\u901A\u8BDD 2.13 \u7B2C\u4E00\u6B21\u89C1\u9762 2.14 \u60C5\u4EBA\u8282\u89C1\u9762 2.27 \u5468\u65E5\u89C1\u9762</p><p>\u51AC\u53BB\u6625\u6765\uFF0C\u4E07\u7269\u8F6E\u8F6C\u3002\u65F6\u95F4\u8FC7\u5F97\u53EF\u771F\u5FEB\u5440\uFF0C\u4ECE\u76F8\u8BC6\u81F3\u4ECA\u8DE8\u8FC7\u4E86 1 \u5E74\uFF0C\u8F6E\u6362\u4E86 2 \u5B63\uFF0C6 \u4E2A\u793C\u62DC\uFF0C42 \u5929\u3002\u90A3\u4E00\u62B9\u62B9\u8BB0\u5FC6\u63A0\u8FC7\u6211\u7684\u5FC3\u6E56\uFF0C\u8361\u6F3E\u51FA\u4E00\u7247\u6D9F\u6F2A\u7684\u6D6A\u82B1\uFF0C\u90A3\u662F\u65E0\u6570\u4E2A\u7CBE\u7075\u95EA\u73B0\u7740\u9752\u6625\u7684\u68A6\u3002</p><p>\u6211\u5F88\u5E78\u8FD0\uFF0C\u5728\u832B\u832B\u4EBA\u6D77\u4E2D\uFF0C\u4E0A\u5929\u5B89\u6392\u4E0E\u4F60\u76F8\u8BC6\u3002\u9047\u89C1\u4F60\u662F\u6211\u8FD9\u4E00\u751F\u7684\u5E78\u8FD0\uFF0C\u611F\u8C22\u4E0A\u5929\u8D50\u7ED9\u6211\u8FD9\u4E48\u4E00\u4E2A\u503C\u5F97\u73CD\u60DC\u7684\u4EBA\u3002</p><p>\u6211\u66FE\u4EF0\u671B\u661F\u7A7A\uFF0C\u770B\u5230\u5212\u8FC7\u7684\u6D41\u661F\u4FBF\u5BF9\u5B83\u8BB8\u4E0B\u613F\u671B\uFF1A\u9047\u4E00\u4EBA\u767D\u9996\uFF0C\u62E9\u4E00\u57CE\u7EC8\u8001\uFF0C\u4E88\u4E00\u5DF1\u771F\u5FC3\uFF0C\u76FC\u4E00\u751F\u5055\u8001\u3002</p><p>\u4E16\u754C\u672C\u662F\u5E73\u51E1\u7684\uFF0C\u800C\u6211\u7684\u4E16\u754C\u56E0\u4E3A\u51FA\u73B0\u4E86\u4E00\u4E2A\u53EF\u7231\u53C8\u7F8E\u4E3D\u7684\u4F60\u4ECE\u6B64\u53D8\u5F97\u7EDA\u4E3D\u591A\u5F69\u3002\u56E0\u4E3A\u4F60\u7684\u51FA\u73B0\u8BA9\u6211\u6709\u4E86\u4E00\u79CD\u671F\u5F85\u7684\u7F8E\u597D\uFF0C\u5F00\u59CB\u671F\u5F85\u5468\u672B\uFF0C\u671F\u5F85\u5047\u671F\uFF0C\u671F\u5F85\u6BCF\u4E00\u4E2A\u548C\u4F60\u89C1\u9762\u7684\u65E5\u5B50\u3002</p><p>\u5728\u548C\u4F60\u8BED\u97F3\u804A\u5929\u3001\u7EBF\u4E0B\u89C1\u9762\u7684\u6BCF\u6BB5\u65F6\u95F4\u91CC\uFF0C\u65F6\u95F4\u5374\u5982\u6D41\u6C34\u822C\u6D88\u901D\u7684\u90A3\u4E48\u5FEB\u3002\u6211\u60F3\u7ED9\u65F6\u95F4\u4E0B\u4E00\u4E2A\u8BC5\u5492\uFF0C\u8BA9\u5B83\u6C38\u8FDC\u7684\u505C\u7559\u5728\u90A3\u4E00\u523B\uFF0C\u8BA9\u6211\u5BF9\u4F60\u7684\u7684\u7231\u548C\u601D\u5FF5\u5F25\u6F2B\u5728\u4E16\u754C\u7684\u6BCF\u4E00\u4E2A\u89D2\u843D\u3002</p><p>\u5982\u4ECA\u6211\u4EF0\u671B\u5929\u7A7A\uFF0C\u90A3\u91CC\u9762\u5168\u662F\u4F60\u7684\u5F71\u5B50\uFF0C\u4F60\u8F7B\u8F7B\u7684\u5FAE\u7B11\u5F15\u6211\u7684\u7075\u9B42\u98DE\u5411\u4E86\u4E5D\u5929\u4E91\u9704\u3002\u6211\u8BB8\u4E0B\u613F\u671B\uFF0C\u4E0D\u8BBA\u4EE5\u540E\u53D1\u751F\u4E86\u4EC0\u4E48\u6211\u90FD\u4F1A\u4E00\u76F4\u548C\u4F60\u5728\u4E00\u8D77\u3002</p><p>\u6211\u559C\u6B22\u4F60\uFF0C\u4E00\u89C1\u949F\u60C5\u7684\u559C\u6B22\u3002</p><p>\u4ECE\u7B2C\u4E00\u6B21\u542C\u5230\u58F0\u97F3\u5C31\u5F00\u59CB\u559C\u6B22\uFF0C\u804A\u5929\u540E\u5C31\u559C\u6B22\u7684\u66F4\u591A\u4E86\uFF0C\u7B2C\u4E00\u6B21\u89C1\u9762\u5C31\u5DF2\u7ECF\u5728\u5FC3\u91CC\u9ED8\u9ED8\u4E0B\u4E86\u51B3\u5FC3\u3002</p><p>\u5728\u8868\u8FBE\u7231\u610F\u7684\u65F6\u5019\uFF0C\u4E16\u95F4\u518D\u7F8E\u7684\u60C5\u8BD7\uFF0C\u4E16\u95F4\u518D\u7F8E\u7684\u793C\u7269\uFF0C\u90FD\u654C\u4E0D\u8FC7\u9762\u5BF9\u9762\u7684\u7B80\u7B80\u5355\u5355\u7684\u56DB\u4E2A\u5B57\u201C\u6211\u559C\u6B22\u4F60\u201D\uFF0C\u90A3\u4EE3\u8868\u5BF9\u8FD9\u4EFD\u7F8E\u597D\u7231\u604B\u7684\u8BDA\u610F\u4EE5\u53CA\u91CD\u89C6\u3002\u9752\u6625\u672C\u8BE5\u52C7\u6562\u800C\u65E0\u754F\uFF0C\u6562\u7231\u53C8\u6562\u6068\u3002\u7231\u60C5\u6E90\u4E8E\u6700\u521D\u5FC3\u7075\u7684\u90A3\u4EFD\u60B8\u52A8\uFF0C\u7231\u60C5\u7684\u5F3A\u8005\uFF0C\u5927\u80C6\u8BC9\u8BF4\u7740\u81EA\u5DF1\u7684\u559C\u6B22\uFF0C\u5448\u73B0\u7740\u7709\u5B87\u95F4\u6E05\u695A\u53EF\u89C1\u7684\u6B22\u559C\u3002</p><p>\u662F\u7684\uFF0C\u6211\u559C\u6B22\u4F60\uFF0C\u4E00\u89C1\u949F\u60C5\u7684\u559C\u6B22\uFF0C\u5F88\u4E45\u5F88\u4E45\u3002</p><p>\u6211\u671F\u5F85\u7740\u672A\u6765\uFF1A</p><p>\u5728\u6BCF\u4E2A\u9192\u6765\u7684\u65E9\u6668\uFF0C\u6211\u7275\u8FC7\u4F60\u7684\u624B\uFF0C\u5C3D\u60C5\u7684\u62E5\u62B1\uFF0C\u62E5\u62B1\u5728\u6BCF\u4E00\u4E2A\u5E0C\u671B\u7684\u6E05\u6668\u3002\u5728\u9759\u8C27\u7684\u591C\u665A\uFF0C\u6211\u4EEC\u5728\u6C99\u53D1\u4E0A\u76F8\u4F9D\u76F8\u504E\uFF0C\u6211\u4EEC\u8C08\u5929\u8BF4\u5730\uFF0C\u9ED8\u9ED8\u5B88\u62A4\u8005\u8FD9\u4EFD\u5E78\u798F\u4E0E\u5B89\u5B81\u3002\u9633\u5149\u660E\u5A9A\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u53BB\u722C\u5C71\u3001\u53BB\u516C\u56ED\u6563\u6B65\u3001\u53BB\u901B\u5546\u573A\uFF0C\u53BB\u5BFB\u5947\u63A2\u79D8\u3001\u53BB\u4F53\u9A8C\u6BCF\u4E00\u4EFD\u4EBA\u95F4\u7F8E\u597D\u3002\u4E0B\u96E8\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u5C31\u4E00\u8D77\u7A9D\u5728\u81EA\u5DF1\u7684\u5C0F\u623F\u95F4\uFF0C\u8046\u542C\u7A97\u5916\u6DC5\u6DC5\u6CA5\u6CA5\u7684\u96E8\u58F0\uFF0C\u4E00\u8D77\u770B\u5C0F\u8BF4\u3001\u770B\u7535\u5F71\u3002</p><p>\u5929\u6C14\u5F88\u597D\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u8FD8\u53EF\u4EE5\u9A7E\u8F66\u5230\u90CA\u5916\uFF0C\u7A7F\u8FC7\u4E00\u7247\u53C8\u4E00\u7247\u7684\u7EFF\u8272\uFF0C\u5728\u7EFF\u836B\u4E0B\uFF0C\u6211\u8E29\u7740\u4F60\u7684\u5F71\u5B50\uFF0C\u8499\u4F4F\u4F60\u7684\u53CC\u773C\uFF0C\u8BA9\u4F60\u731C\u6211\u4EEC\u672A\u6765\u7684\u5BB6\u5728\u54EA\u4E2A\u65B9\u5411\uFF0C\u6211\u4EEC\u653E\u8086\u5730\u6B22\u7B11\uFF0C\u9759\u9759\u5730\u8EBA\u7740\uFF0C\u9ED8\u9ED8\u5730\u770B\u7740\u5929\u7A7A\u3002\u5982\u679C\u6B64\u65F6\u4F60\u9700\u8981\u6211\uFF0C\u6211\u4F1A\u7D27\u7D27\u63E1\u4F4F\u4F60\u7684\u624B\u4E0D\u653E\u5F00\u3002</p><p>\u6211\u4E0D\u7231\u72EC\u81EA\u65C5\u884C\uFF0C\u81EA\u4ECE\u9047\u89C1\u4F60\u4E4B\u540E\u5C31\u60F3\u548C\u4F60\u4E00\u8D77\u53BB\u5F88\u591A\u5730\u65B9\uFF0C\u60F3\u548C\u4F60\u4E00\u8D77\u770B\u5F88\u591A\u7F8E\u4E3D\u7684\u98CE\u666F\u3002\u751F\u547D\u7684\u6BCF\u4E00\u7A0B\u90FD\u60F3\u4E0E\u4F60\u5206\u4EAB\uFF0C\u4E16\u754C\u7684\u6BCF\u4E00\u5904\u90FD\u60F3\u548C\u4F60\u8D70\u904D\uFF0C\u751F\u6D3B\u7684\u6BCF\u4E00\u5929\u90FD\u60F3\u6709\u4F60\u7684\u75D5\u8FF9\u3002\u5982\u679C\u6709\u4E00\u5929\u6211\u8D70\u4E0D\u52A8\u4E86\uFF0C\u90A3\u4E48\u4F60\u5C31\u662F\u6211\u773C\u91CC\u770B\u4E0D\u591F\u7684\u98CE\u666F\u3002</p><p>\u6211\u542C\u8BF4\u897F\u85CF\u7684\u5929\u7A7A\u5F88\u5E72\u51C0\uFF0C\u5F88\u5723\u6D01\uFF0C\u90A3\u91CC\u662F\u7075\u9B42\u7684\u907F\u96BE\u6240\uFF0C\u90A3\u91CC\u662F\u751F\u547D\u81F3\u9AD8\u65E0\u4E0A\u7684\u8BC9\u6C42\u3002\u6211\u60F3\u5E26\u7740\u6211\u4EEC\u7684\u7231\u60C5\u4E0E\u4F60\u4E00\u8D77\u5954\u8D74\u90A3\u91CC\uFF0C\u4E3A\u6211\u4EEC\u4E24\u4E2A\u5351\u5FAE\u6E3A\u5C0F\u7684\u751F\u547D\u5BFB\u6C42\u4E00\u4EFD\u5F52\u5C5E\uFF0C\u5C06\u6211\u4EEC\u7684\u8654\u8BDA\u7559\u5728\u79BB\u5929\u7A7A\u6700\u8FD1\u7684\u5730\u65B9\uFF0C\u90A3\u6837\u4E0D\u7BA1\u6211\u4EEC\u8D70\u5230\u54EA\u91CC\uFF0C\u6211\u4EEC\u53EA\u8981\u770B\u5929\u5C31\u4F1A\u770B\u5230\u5F7C\u6B64\u7684\u5FC3\u3002</p><p>\u6211\u60F3\uFF0C\u53EA\u8981\u6709\u4F60\uFF0C\u751F\u547D\u5C31\u6709\u6D3B\u7684\u5E78\u798F\uFF0C\u751F\u6D3B\u5C31\u4ECE\u4E0D\u67AF\u71E5\uFF1B\u53EA\u8981\u6709\u4F60\uFF0C\u54EA\u6015\u5386\u7ECF\u98CE\u96E8\uFF0C\u6211\u4E5F\u7518\u4E4B\u5982\u9974\u3002</p><p>\u5F15\u8A00\uFF1A\u201C\u65E2\u89C1\u4F0A\u4EBA\uFF0C\u5728\u6C34\u4E00\u65B9\uFF0C\u6EAF\u6D04\u4ECE\u4E4B\uFF0C\u4E0D\u60E7\u4E09\u5343\u91CC\u6CB3\u957F\uFF0C\u4E0D\u754F\u4E07\u91CD\u5C71\u963B\u6321\u3002\u201D</p><p>\u7231\u4F60\u7684\u5C0F\u6C11\u3000 2022\u5E743\u67086\u65E5</p>",20),r=[_];function s(a,n){return e(),t("div",null,r)}var i=p(c,[["render",s],["__file","love.html.vue"]]);export{i as default};
